import { createSlice } from "@reduxjs/toolkit";
import { uiActions } from "./ui-slice";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addToCart(state, action) {
      const newItems = state.items.slice();
      const existingItem = newItems.find(
        (item) => item.id === action.payload.id
      );

      if (existingItem) {
        existingItem.totalAmount++;
        existingItem.totalPrice = existingItem.totalPrice + existingItem.price;
      } else {
        const newItem = {
          id: action.payload.id,
          name: action.payload.title,
          price: action.payload.price,
          totalAmount: 1,
          totalPrice: action.payload.price,
        };
        newItems.push(newItem);
      }
      state.items = newItems;
      state.totalQuantity++;
    },
    removeFromCart(state, action) {
      const newItems = state.items.slice();
      const existingItem = newItems.find((item) => item.id === action.payload);
      state.totalQuantity--;

      if (existingItem.totalAmount === 1) {
        const filteredArray = newItems.filter(
          (item) => item.id !== action.payload
        );
        state.items = filteredArray;
      } else {
        existingItem.totalAmount--;
        existingItem.totalPrice = existingItem.totalPrice - existingItem.price;
      }
    },
  },
});

const sendCartData = (cart) => {
  return async (dispatch) => {
    dispatch(
      uiActions.showNotification({
        status: "pending",
        title: "Sending...",
        message: "Sending cart data!",
      })
    );
    const sendRequest = async () => {
      const response = await fetch(
        "https://react-http-de4ad-default-rtdb.europe-west1.firebasedatabase.app/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
    }
    try {
      await sendRequest();
    } catch (error) {

    }
    
    
    dispatch(
      uiActions.showNotification({
        status: "success",
        title: "Success!",
        message: "Sent cart data successfully!",
      })
    );
  };
};

export const cartActions = cartSlice.actions;

export default cartSlice;
